
"use strict"




const chooseWords = require('rpgen/crypto/choose-words')





const rpgen = rawArgs => {

	const args = rpgen.preprocess(rpgen.validate(rawArgs))

	chooseWords(4, words => {
		console.log(words)
	})

}

rpgen.preprocess = rawArgs => {
	return rawArgs
}

rpgen.validate = rawArgs => {
	return rawArgs
}





module.exports = rpgen
