#!/usr/bin/env node

"use strict"




const doc = `
Name:
	rpgen — Generate a high entropy, memorable password.

Usage:
	rpgen [-w WORDS | --words WORDS] [-n NUM | --num NUM] [-p | --password-only]
	rpgen [-n NUM | --num NUM] [-p | --password-only] [--] -
	rpgen (-h | --help | --version)

Description:
	Rpgen — Random password generator — chooses random words from an input
	dictionary, to use as a password.

Security:
	Random words are selected using a cryptographically secure random number
	generator derived from the Node.js builtin 'crypto.randomBytes'. This RNG is verified against the 'dieharder' test suite.

Options:
	-                          Read words from standard input.
	-w WORDS, --words WORDS    A path containing the words to derive the password from. [default: data/words.txt].
	-n NUM, --num NUM          The number of words to choose. [default: 5]
	-p, --password-only        Do not display the password entropy.
	-h, --help                 Display this documentation.

Arguments:

`





const docopt = require('docopt').docopt
const rpgen  = require('rpgen/app/rpgen')
const args   = docopt(doc)





rpgen(args)
