
"use strict"




const fs        = require('fs')
const readline  = require('readline')




const readWords = (fpath, callback) => {

	var lines = [ ]

	readline.createInterface({
		input: fpath ? fs.createReadStream(fpath) : process.stdin
	})
	.on('line', line => {
		lines.push(line)
	})
	.on('close', ( ) => {
		callback(null, lines)
	})

}





module.exports = readWords
