const readWords = (fpath, callback) => {

	if (fpath) {
		readWords.fs(fpath, callback)
	} else {
		readWords.stdin(callback)
	}

}

readWords.fs = (fpath, callback) => {
	fs.readFile(fpath, callback)
}

